INSERT INTO `pre_article` VALUES ('9', '农夫乐园圣诞月价格优惠调整通知', '<span style=\"color:#888888;font-family:微软雅黑;background-color:#F4F4F4;\">圣诞月：农夫乐园门市价成人120元，儿童60元 调整后优惠价:成人60元 儿童40元</span>', 'static/images/201603301238276166765.jpg!182110', '1485446400', '重要通知', '<p>圣诞月：农夫乐园门市价成人120元，儿童60元&nbsp;</p><p>调整后优惠价:成人60元 儿童40元（1.5米以下儿童、60周岁以上老年人、学生）</p><p>免票政策：70周岁以上老年人、军人、教师、残疾、导游凭相关证件享受免票优惠。</p><p>优惠价执行时间：2016年12月2日起。</p><p>另：农夫乐园11月27日恐龙展已经结束，目前保持常规项目正常营业。</p><p style=\"text-align: right;\">农夫乐园</p><p style=\"text-align: right;\">2016年12月1日</p><p><br></p>', '2');
INSERT INTO `pre_article` VALUES ('10', '农夫乐园动漫欢乐嘉年华！票价3折优惠！', '<span style=\"color:#888888;font-family:微软雅黑;background-color:#F4F4F4;\">2016年11月农夫乐园开业8周年，现推出动漫欢乐嘉年华活动，这里有“侏罗纪再现”的大型恐龙展，有让您乐不思归的“萌宠总动员”，每周末更是有百人COSPALY表演，除此以外农夫乐园常规免费项目三大免费表演：小动物表演，农家三绝，农夫运动会；免费8大游乐项目摇头飞椅、弯月飞车、海盗船等等让您HAPPY到爆。这还不够！11月5日-27日份农夫乐园持续8年门的市价120元将被打破，调至3折!</span>', 'static/uploads/201704130523068655.jpg', '1485446400', '重要通知', '<p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135170408757.jpg\" style=\"float: none; width: 691px; height: 946px;\" title=\"TWHD_01.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135192640029.jpg\" style=\"float: none; width: 688px; height: 894px;\" title=\"TWHD_02.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135214461277.jpg\" style=\"float: none; width: 690px; height: 824px;\" title=\"TWHD_03.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135217341442.jpg\" style=\"float: none; width: 690px; height: 742px;\" title=\"TWHD_04.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135227832042.jpg\" style=\"float: none; width: 689px; height: 414px;\" title=\"TWHD_05.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135230652203.jpg\" style=\"float: none; width: 688px; height: 859px;\" title=\"TWHD_06.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135076543389.jpg\" style=\"float:none;\" title=\"CGNR_01.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135105385038.jpg\" style=\"float:none;\" title=\"CGNR_02.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135107695170.jpg\" style=\"float:none;\" title=\"CGNR_03.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/201611141135124546134.jpg\" style=\"float: none; width: 689px; height: 1019px;\" title=\"CGNR_04.jpg\"></p><p><img src=\"http://officialsite.juyingzhiye.com/2016/2016111411351331866